renderHTML:
__ht__function(data, gv, cache) {
    if (!cache.g3d) {
        var g3d = cache.g3d = new ht.graph3d.Graph3dView();

        if (data.a('eye')) g3d.setEye(data.a('eye'));
        if (data.a('center')) g3d.setCenter(data.a('center'));
        if (data.a('far')) g3d.setFar(data.a('far'));
        if (data.a('near')) g3d.setNear(data.a('near'));

        g3d.layoutHTML = function() {
            gv.layoutHTML(data, g3d, true);
        };
    }
    var sceneURL = data.a('sceneURL');
    if (cache.g3d.sceneURL !== sceneURL) {
        cache.g3d.dm().clear();
        cache.g3d.sceneURL = sceneURL;
        if (sceneURL) {
            ht.Default.xhrLoad(sceneURL, function(json) {
                cache.g3d.dm().deserialize(json);
            });
        }
    }
    return cache.g3d;
}
